import React, { useState, useEffect } from "react";
import ProductDataService from "../Services/Barcelparts.js"


const Research_Page = function (products_to_display) {
  const [products, setProducts] = useState([]);
  const [searchName, setSearchName] = useState("");




  const onChangeSearchName = e => {
    const searchName = e.target.value;
    setSearchName(searchName);
  };

  const getAll = () => {
    ProductDataService.getAll()
      .then(response => {
        console.log(response.data);
        setProducts(response.data.products);
      })
      .catch(e => {
        console.log(e);
      });
  };




  const find = (query, by) => {
    ProductDataService.find(query, by)
      .then(response => {
        console.log(response.data);
        setProducts(response.data.products);
        // Trying to filter the data still experimenting
        products = products.map((product, index) => {
          if (product.PrecoCusto == "0,00") {
            products.splice(index,1);
            console.log("Deleted ",index)
            console.log(products);
          }
        })
      })
      .catch(e => {
        console.log(e);
      });
  };

  const findByName = () => {
    find(searchName, "name")
  };


