  console.log(search.search_button)
  useEffect(() => {
    find(search.search, "name")
  }, [search.search_button])

  return (
   <div>
      <div className="row pb-1">
        <div className="input-group col-lg-4">
          <input
            type="text"
            className="form-control"
            placeholder="Search by name"
            value={searchName}
            onChange={onChangeSearchName}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByName}
            >
              Search
            </button>
          </div>
        </div>

      </div>

      <div className="row">
        {products_to_display.map((products) => {
          return (
            <div className="col-lg-4 pb-1">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{products.Design}</h5>
                  <p className="card-text">
                    <strong>Producer: </strong>{products.Marca}<br />
                    <strong>Price: </strong>{products.PrecoCusto}<br />
                  </p>
                </div>
              </div>
            </div>
          );
        })
        }
      </div>
    </div>
  );
}


export default Research_Page;
